import React, { useState } from "react";
import {
  Typography, Button, Grid, Checkbox, TextField, OutlinedInput,
  FormControl, InputLabel, InputAdornment, IconButton
} from "@material-ui/core"
import Show from '@material-ui/icons/Visibility';
import Hide from '@material-ui/icons/VisibilityOff';
import SharpIcon from '@material-ui/icons/SendSharp';

import IntlTelInput from 'react-intl-tel-input';
import "react-intl-tel-input/dist/main.css"

import { makeStyles } from '@material-ui/core/styles';

// CSS :
import "../CSS/SignUp.scss";

const Style = makeStyles({
  btn: {
    width: "100%",
    background: "red",
    height: "3rem",
    color: "#fff",
    "&:hover": {
      color: "red"
    }
  }
})


const SignUp = () => {
  const classes = Style();


  // Form Data Control :
  const [enteredData, updateEnteredData] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    confirmPassword: ""
  })


  const [error, updateError] = useState({
    firstnameError: "",
    lastnameError: "",
    emailError: "",
    passwordError: "",
    confirmPasswordError: ""
  })

  const enteringData = (event) => {

    const { value, name } = event.target;

    // updateEnteredData((preValue) => {
    //   return {
    //     ...preValue,
    //     [name]: value
    //   }
    // })

    switch (name) {
      case "firstname":
        updateEnteredData((preValue) => {
          return {
            ...preValue,
            [name]: value
          }
        })
        if (!enteredData.firstname || enteredData.firstname.length < 3) {
          updateError((preValue) => {
            return {
              ...preValue,
              firstnameError: "First Name Must be more than 3 Characters"
            }
          })
        } else {
          updateError((preValue) => {
            return {
              ...preValue,
              firstnameError: ""
            }
          })
        }
        console.log("OnChange = ", enteredData)
        break;

      case "lastname":
        updateEnteredData((preValue) => {
          return {
            ...preValue,
            [name]: value
          }
        })
        if (!enteredData.lastname || enteredData.lastname.length < 3) {
          updateError((preValue) => {
            return {
              ...preValue,
              lastnameError: "Last Name Must be more than 3 Characters"
            }
          })
        } else {
          updateError((preValue) => {
            return {
              ...preValue,
              lastnameError: ""
            }
          })
        }
        break;

      default:
        console.log("Sonthing went wrong")
        break;
    }

    // if (!enteredData.firstname || enteredData.firstname.length < 3) {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       firstnameError: "First Name Must be more than 3 Characters"
    //     }
    //   })
    // } else {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       firstnameError: ""
    //     }
    //   })
    // }

    // if (!enteredData.lastname || enteredData.lastname.length < 3) {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       lastnameError: "Last Name Must be more than 3 Characters"
    //     }
    //   })
    // } else {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       lastnameError: ""
    //     }
    //   })
    // }

    // if (/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(enteredData.email)) {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       emailError: ""
    //     }
    //   })
    // } else {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       emailError: "Invalid Email"
    //     }
    //   })
    // }

    // if (!enteredData.password || enteredData.password.length < 8) {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       passwordError: "Password Must be more than 8 Characters"
    //     }
    //   })
    // } else {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       passwordError: ""
    //     }
    //   })
    // }
    // if (enteredData.confirmPassword != enteredData.password) {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       confirmPasswordError: "Password Does not Match"
    //     }
    //   })
    // } else {
    //   updateError((preValue) => {
    //     return {
    //       ...preValue,
    //       confirmPasswordError: ""
    //     }
    //   })
    // }
  }


  // Show Hide Password :
  const [showPV, updateShowPV] = useState({
    showP: false
  })
  const [showCPV, updateShowCPV] = useState({
    showCP: false
  })

  const clikShowP = () => {
    updateShowPV({
      showP: !showPV.showP
    })
  }
  const clikShowCP = () => {
    updateShowCPV({
      showCP: !showCPV.showCP
    })
  }


  return (
    <>
      <div className="signup_container">
        <div className="signup_box">
          <Typography variant="h5" style={{ textAlign: "center" }}>
            SIGN-UP
        </Typography>

          <form className="form">
            <TextField
              label="First Name"
              variant="outlined"
              name="firstname"
              onChange={enteringData}
              style={{ margin: "1rem 0", width: "100%" }}
            />
            <TextField
              label="Last Name"
              variant="outlined"
              name="lastname"
              onChange={enteringData}
              style={{ width: "100%", marginBottom: "1rem" }}
            />

            <IntlTelInput
              preferredCountries={["pk"]}
            />

            <TextField
              label="Email"
              variant="outlined"
              name="email"
              onChange={enteringData}
              style={{ width: "100%", marginTop: "1rem" }}
            />

            <FormControl style={{ margin: "1rem 0", width: "100%" }} variant="outlined">
              <InputLabel>Password</InputLabel>
              <OutlinedInput
                name="password"
                onChange={enteringData}
                type={showPV.showP ? "text" : "password"}
                labelWidth={70}
                endAdornment={
                  <InputAdornment position="end">
                    <IconButton edge="end" onClick={clikShowP}>
                      {showPV.showP ? <Show /> : <Hide />}
                    </IconButton>
                  </InputAdornment>
                }
              />

            </FormControl>
            <FormControl style={{ margin: "1rem 0", width: "100%" }} variant="outlined">
              <InputLabel>Confirm Password</InputLabel>
              <OutlinedInput
                name="confirmPassword"
                onChange={enteringData}
                type={showCPV.showCP ? "text" : "password"}
                labelWidth={130}
                endAdornment={
                  <InputAdornment position="end">
                    <IconButton edge="end" onClick={clikShowCP}>
                      {showCPV.showCP ? <Show /> : <Hide />}
                    </IconButton>
                  </InputAdornment>
                }
              />

            </FormControl>

            <Button
              variant="contained"
              type="submit"
              endIcon={<SharpIcon />}
              className={classes.btn}
            >
              SIGN UP
          </Button>

          </form>
        </div>
      </div>
      {error.firstnameError} <br/>
      {error.lastnameError} <br/>
      {error.emailError} <br/>
      {error.passwordError} <br/>
    </>
  )
}

export default SignUp
