import React from 'react'

import './Slider.css'

const slider_container = document.querySelector('.slider_container');
const slider_container_img = document.querySelectorAll('.slider_container img');

let counter = 1;
const size = slider_container_img[0].clientWidth;

slider_container.style.transform = 'tarnslateX(' + (-size * counter) + 'px)';

const next = () => {
    slider_container.style.transition = "transform 0.4s ease-in-out";
    counter++;
    slider_container.style.transform = 'tarnslateX(' + (-size * counter) + 'px)';

}


const Slider = () => {
    return (
        <>
            <div className="slider">
                <div className="slider_container">
                    <img src="img/slider1.jpg" alt="ERROR" />
                    <img src="img/slider2.jpg" alt="ERROR" />
                    <img src="img/slider3.jpg" alt="ERROR" />
                </div>
            </div>
            <button id="prevbtn">prev</button>
            <button id="nextbtn">next</button>
        </>
    )
}

export default Slider;
