import React, { useState } from 'react'


import './Slider.css'
import './Slider.scss'

import img1 from "./img/slider1.jpg";

const Img = (prop)=>{
    return(
        <>
            <img src={prop} alt="ERROR"/>
        </>
    )
}

const Slider = () => {

    let imgArray = [1, 2, 3, 4, 5]
    const [x, setx] = useState(0);

    const prev = () => {
        x == 0 ? setx(-100 * (imgArray.length - 1)) : setx(x + 100);
    }
    const next = () => {
        (x == -100 * (imgArray.length - 1)) ? setx(0) : setx(x - 100);
    }


    return (
        <>
            <div className="slider_container">
                {
                    imgArray.map((item, index) => {
                        return (
                            <>
                                <div className="slider" key={index} style={{ transform: `translateX(${x}%)` }}>
                                    {item}
                                </div>
                            </>
                        )
                    })
                }

                <button id="prevbtn" onClick={prev}>prev</button>
                <button id="nextbtn" onClick={next}>next</button>
            </div>

        </>
    )
}

export default Slider;
